import React, { Component } from 'react'
import NewsItem from './NewsItem'
import PropTypes from 'prop-types'


export class Newss extends Component {
    static defaultProps={
      country:"in",
      pageSize:"5",
      category:"general"
    }
    static propTypes={
      country:PropTypes.string,
      pageSize:PropTypes.number,
      category:PropTypes.string
    }
    
    constructor(){
        super();
        this.state={
         articles:[],
         loading:false,
         page:1
        }
    }
    async updateNews(pageNo){
      let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}
      &apiKey=221a5977d9644fed85fa0b6a92a438d9&page1=${this.State.pageNo}&pageSize=${this.props.pageSize} `;
        let data=await fetch(url);
        let parsedData=await data.json();
        console.log(parsedData);
        this.setState({articles:parsedData.articles,totalResults:parsedData.totalResults})
    }
     
     async componentDidMount(){
        let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=221a5977d9644fed85fa0b6a92a438d9&page1&pageSize=${this.props.pageSize} `;
        let data=await fetch(url);
        let parsedData=await data.json();
        console.log(parsedData);
        this.setState({articles:parsedData.articles,totalResults:parsedData.totalResults})
     }

     handlePrevClick=async()=>{
        //console.log("Previous");
        //let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=221a5977d9644fed85fa0b6a92a438d9&page=${this.state.page-1}&pageSize=${this.props.pageSize}`;
        //let data=await fetch(url);
        //let parsedData=await data.json();
        //console.log(parsedData);
        //this.setState({
           // page:this.state.page-1,
            //articles:parsedData.articles
        //})
        this.setState({page:this.State.page-1})
        this.updateNews();

     }

     handleNextClick=async()=>{
        //console.log("Next");
        //if(this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pageSize)){

        //}
        //else{
        //let url=`https://newsapi.org/v2/top-headlines?country=${this.props.country}&category=${this.props.category}&apiKey=221a5977d9644fed85fa0b6a92a438d9&page=${this.state.page+1}&pageSize=${this.props.pageSize}`;
        //let data=await fetch(url);
        //let parsedData=await data.json();
        //console.log(parsedData);
        //this.setState({
            //page:this.state.page+1,
            //articles:parsedData.articles
        
        //})

    //}
    this.setState({page:this.State.page+1})
    this.updateNews();
     }
  render() {
    return (
      <div className="container my-3">
        <h1 className="text-center">NewsApp-Top headlines </h1>      
        <div className="row">
            {this.state.articles.map((element)=>{
               return <div className="col-md-4" key={element.url}>
               <NewsItem title={element.title?element.title.slice(0,45):""} description={element.description?element.description.slice(0,88):""}
                imageUrl={element.urlToImage} newsUrl={element.url} author={element.author}
                date={element.publishedAt} source={element.source.name}/>
            
            </div>
            })}
            </div>
            < div className="container d-flex justify-content-between">
            <button disabled={this.state.page<=1}type="button" className="btn btn-dark" onClick={this.handlePrevClick} >&larr;Previous</button>
            <button disabled={this.state.page+1 > Math.ceil(this.state.totalResults/this.props.pageSize)}type="button" className="btn btn-dark" onClick={this.handleNextClick}>Next &rarr;</button>
            </div>
            </div>
   
    )
  }
}

export default Newss
